/**
 * It simply convert a string of a list in a mutableList of Float to be shown in the graph
 */
fun pulisciListaXGrafici(strgLista: String): MutableList<Float> {
    val listaPulita = strgLista.replace("[^-0-9,. ]".toRegex(), "")
    val divisa: List<String> = listaPulita.split(", ")
    val convertita: MutableList<Float> = divisa.map { it.toFloat() }.toMutableList()
    return convertita
}

/**
 * To convert pressure values
 */
fun convertiPressione(numero:Int):Int{
    if (numero==0){return 15}
    else{return (numero*-1)+15}
}

//funzione che aggiusta selettivamente valori anomali con un crosscheck tra serie liquidi
//MA C'è ANCORA BISOGNO DI QUESTO AGGIUSTAMENTO??
//ADESSO LA SERIE TOTALE HA DATI PER OGNI MINUTO) - quindi si dovrebbe togliere questa funzione di aggiustamento?
fun comparaRaccoltaLiquidi(totale: MutableList<Float>, oraria: MutableList<Float>): Pair<MutableList<Float>, MutableList<Float>>{
    //calcola differenza con
    Log.d("giuseppeFix", "size ${totale.size} serie totale: $totale")

    //si crea una serie di differenze tra numeri e si aggiunge uno zero all'inizio - DIFFERENZE TRA DUE NUMERI CONSECUTIVI
    var diffTotale = mutableListOf<Float>(0f)+ totale.windowed(2,1) {it-> it[1]-it[0]}.toMutableList()
    Log.d("giuseppeFix", "Differenza size ${diffTotale.size} serie totale: $diffTotale")

    //SI CREA UNA LISTA COMPOSTA DA (VALORE SINGOLO ORARIO (MAX 150 ore) - 65536) complemento a 65536, sempre negativo!!!!
    //DELLA SERIE ORARIA DEI LIQUIDI - "RACCOLTA LIQUIDO PER ORA"
    var diffOraria = oraria.map { it-> it-65536 }.toMutableList()
    Log.d("giuseppeFix", "serie oraria: $oraria")
    Log.d("giuseppeFix", "Differenza oraria size ${diffOraria.size} serie totale: $diffOraria")

    /*AGGIUSTAMENTI: ADESSO L'INDICE E' 1511 E LA DIMENSIONE INVECE E' 150
        La dimensione della serie oraria adesso e' 150!!! per ogni ora (ORARIA)
    */
    diffTotale.mapIndexed{ index, it->
        if((it<0)&&(it==diffOraria[index]) ){
            Log.d("giuseppeFix", "trovato un valore $it");
            totale[index-1]+=it; oraria[index]=0f  }
    }
    //Log.d("giuseppeFix", "prima lista aggiustata $totale")
    //Log.d("giuseppeFix", "seconda lista aggiustata $oraria")

    //salva la serie totale corretta
    //modificaRiga(uriFile!!, 7,"Raccolta liquidi totale: ${totale.joinToString(", ")}",false )
    //ed aggiusta i valori le serie (comprese quelle temporanee)
    //dbLiquidLevelTemp = totale
    //dbLiquidLeakages = oraria
    val coppiaLocale = Pair<MutableList<Float>, MutableList<Float>>(totale, oraria)
    return coppiaLocale
}

//calcola i valori delle ultime 24 ore
fun calcoloLiquidiGiornoLive(list: MutableList<Float>):Pair<Int,String>{
    var lista = mutableListOf<Float>()
    lista = if(list.size>24){ list.takeLast(24).toMutableList() } else { list }
    val totale = lista.sum().toString()
    val coppia = Pair(2, totale)
    return coppia
}


/**
 * It makes a new serie from a previous one that represents its summatory
 */
fun calcolaSommatoria(serie: MutableList<Float>){
    var numeroTotale: Float = 0f
    val raccoltaTotale: MutableList<Float> = mutableListOf()
    serie.map { numeroTotale+=it; raccoltaTotale.add(numeroTotale) }
    //per verificare dimensioni e ultimo valore
    Log.d("giuseppeRaccolta", "PARAGONE: DIMENSIONI UGUALI: ${serie.size==raccoltaTotale.size}" +
            " ULTIMO VALORE ${serie.last()} e ${raccoltaTotale.last()} ")
}

